mapper.select:
SELECT

FROM category


        AND name LIKE CONCAT('%', #{params.name}, '%')


        AND level = #{params.level}


        AND status = #{params.status}


        AND parent_id = #{params.parentId}


ORDER BY level ASC, id ASC

mapper.delete:
WITH RECURSIVE category_tree AS ( SELECT id FROM category WHERE id IN
#{id}
UNION ALL SELECT c.id FROM category c JOIN category_tree ct ON c.parent_id = ct.id ) DELETE FROM category WHERE id IN (SELECT id FROM category_tree)

mapper.select:
SELECT COUNT(*)
FROM products
WHERE category_id = #{categoryId}

SELECT COUNT(*)
FROM category
WHERE parent_id = #{categoryId}
