mapper.select:
SELECT

FROM coupon
WHERE status = 1
AND num > 0
AND start_time <= #{now}
AND end_time >= #{now}

    AND min_amount <= #{amount}

ORDER BY value DESC

mapper.update:
UPDATE coupon
SET num = num - #{count},
    update_time = NOW()
WHERE id = #{id}
AND num >= #{count}

mapper.select:
SELECT

FROM coupon
WHERE id = #{id}
AND status = 1
AND num > 0
AND start_time <= #{now}
AND end_time >= #{now}
AND min_amount <= #{amount}

mapper.delete:
DELETE FROM coupon
WHERE id IN

    #{id}
