mapper.update:
UPDATE favorite_folder
name = #{name}, description = #{description}, is_public = #{isPublic}, update_time = NOW()
WHERE id = #{id} AND user_id = #{userId}

UPDATE favorite_folder 
SET item_count = (
    SELECT COUNT(*) 
    FROM favorite_item 
    WHERE folder_id = #{folderId}
)
WHERE id = #{folderId}

mapper.select:
SELECT f.*, u.username FROM favorite_folder f JOIN users u ON f.user_id = u.id WHERE f.is_public = 1
AND (f.name LIKE CONCAT('%', #{keyword}, '%') OR f.description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY f.item_count DESC LIMIT #{limit}

mapper.update:
UPDATE favorite_folder
SET sort = #{newSort},
    update_time = NOW()
WHERE id = #{folderId} 
AND user_id = #{userId}
